#include "../../includes/minishell.h"

int	ft_strlen_exp(char *s)
{
	int	i;

	i = 0;
	if (!s)
		return (0);
	while (s[i])
	{
		if (s[i] == '=')
			return (i);
		++i;
	}
	return (i);
}
